import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart, updateCartItem } from '../redux/actions/cartAction'
import { Modal, Button } from 'react-bootstrap';

const Cart = ( { show, onHide }) => {
  const cartItems = useSelector((state) => state.cart.cartItems);
  const dispatch = useDispatch();

  const handleRemoveFromCart = (item) => {
    dispatch(removeFromCart(item));
  };

  const handleIncrement = (item) => {
    dispatch(updateCartItem({ ...item, quantity: item.quantity + 1 }));
  };

  const handleDecrement = (item) => {
    if (item.quantity > 1) {
      dispatch(updateCartItem({ ...item, quantity: item.quantity - 1 }));
    }
  };

  return (
    <Modal show={show} onHide={onHide} animation={false}>
      <Modal.Header closeButton>
        <Modal.Title>Your Cart</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Cart content rendering here */}
        <div>
<h2>Shopping Cart</h2>
{cartItems.length === 0 ? (
  <p>Your cart is empty.</p>
) : (
  <ul>
    {cartItems.map((item) => (
      <li key={item.id}>
        {item.title} - ${item.price} x {item.quantity}{' '}
        <button className='btn btn-primary' onClick={() => handleIncrement(item)}>+</button>
        <button onClick={() => handleDecrement(item)}>-</button>
        <button onClick={() => handleRemoveFromCart(item)}>Remove</button>
      </li>
    ))}
  </ul>
)}
</div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default Cart;
