import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { addToCart } from '../redux/actions/cartAction';

const ProductComponent = () => {
  const product = useSelector((state) => state.allProducts.product);
  const dispatch = useDispatch();

  const handleAddToCart = (product) => {
    dispatch(addToCart({ ...product, quantity: 1 }));
  };

  return (
    <div className="four column row">
    
        <div className="column" key={product.id} style={{ marginTop: '50px' }}>
          <div className="ui link cards">
            <div className="card">
              <div className="image">
                {/* <img src={product.image} alt={product.title} style={{ height: '300px' }} /> */}
              </div>
              <div className="content" style={{ height: 'fit-content' }}>
                <div className="header">{product.title}</div>
                <div className="meta price">${product.price}</div>
                <div className="meta category">{product.category}</div>
              </div>
              <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          </div>
        </div>
     
    </div>
  );
};

export default ProductComponent;
