// Header.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Cart from './Cart'; // Import the Cart component
import { FaShoppingCart } from 'react-icons/fa'; // You can use a shopping cart icon library
import Badge from '@mui/material/Badge';
import MailIcon from '@mui/icons-material/Mail';

const Header = () => {
  const [showCartModal, setShowCartModal] = useState(false);

  const handleCartIconClick = () => {
    setShowCartModal(true);
  };

  const handleCloseCartModal = () => {
    setShowCartModal(false);
  };

  return (
    <div className='ui fixed menu'>
      <div className='ui container center'>
        <h2>Fake Shop</h2>
        <Link to="#" onClick={handleCartIconClick}>
          <FaShoppingCart /> {/* Add your cart icon here */}
        </Link>
      </div>
      <Cart show={showCartModal} onHide={handleCloseCartModal} />
    </div>
  );
};

export default Header;
