import React, { useState } from 'react';

function SearchBar() {
  const [focus, setFocus] = useState(false);

  const toggleFocus = () => {
    setFocus((prevFocus) => !prevFocus);
  };

  return (
    <div className="app">
      <div className="container">
        <input
          type="text"
          className={focus ? 'focused' : ''}
          placeholder="Search"
        />
        <button
          onClick={toggleFocus}
          id="search-button"
          className={focus ? 'active' : ''}
        >
          üîç ü§ê
        </button>
      </div>
    </div>
  );
}

export default SearchBar;
