import { ActionTypes } from "../contants/action-types";
const initialState = {
    products:[
        {
            id:1,
            title:"Sathwik",
            category:'Developer',
        }
    ] 
}
export const productReducer = (state = initialState,{type,payload}) => {
    switch (type) {
        case ActionTypes.SET_PRODUCTS:
            return {...state, products:payload};
            case ActionTypes.SET_PRODUCTS:
            return {...state, products:payload};
        default:
            return state;
}
}
