import { createStore, applyMiddleware } from 'redux';
import { composeWithDevTools } from 'redux-devtools-extension/developmentOnly'; // Import this from redux-devtools-extension

import reducers from './reducers/index.js';

const store = createStore(
  reducers,
  composeWithDevTools(applyMiddleware(/* any middleware you need */))
);

export default store;
