import React from 'react';
import { Link  } from 'react-router-dom';
import { useSelector } from 'react-redux';

const ProductComponent = () => {
    const products = useSelector((state)=>state.allProducts.products)
    let renderList = products.map((product)=>{
        const  {id,title,image,price,category} = product 
        return(
            <>
              <Link to = {`/product/${id}`} />
             <div className=' column ' key={id} style={{marginTop:'50px'}}>
              
             <div className='ui link cards'>
                <div className='card'>
                    <div className='image'>
                        <img src={image} alt={title} style={{height:'400px'}}/>
                    </div>
                    <div className='content' style={{height:'200px'}}>
                        <div className='header'>{title}</div>
                        <div className='meta price'>{price}</div>
                        <div className='meta category'>{category}</div>
                    </div>
                </div>
             </div> 
             <Link/>
        </div>

            </>
        )
    })
 
    return (
       <>
       <div className="four column row">
       {renderList}
       </div>
       
       </>
    );
};

export default ProductComponent;
